import "./Card.css";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Card(props) {
  return (
    <div className="card">
      <div className="content">
        <img className="card_image" src={props.url} alt={props.name} />
        <div className="text">
          <h2 className="card__title">
            <b>{props.name}</b>
          </h2>
          <p className="card__mrp">
            MRP: <b>{props.mrp}</b>
          </p>
          <p className="card__description">{props.discription}</p>
          <button className="card__button btn btn-primary">Buy Now</button>
        </div>
      </div>
    </div>
  );
}
