import './style/BooksCard.css';
import { useLocation } from 'react-router-dom';

function BooksCard() {
  // const location = useLocation();
  const { message } = useLocation().state || {};
  const props = message
  console.log(props);
  return (
    <div className="BooksCard vh-100">
      <div className="ComicCard__Wrapper">
      <div className="ComicCard__ImageWrapper">
        <img alt={props.name} className="comic-image" src={"url"} />
      </div>
      <div className="ComicCard__Content">
        <h1 className="ComicCard__Title">{"bookName"}</h1>
        <div className="ComicCard__Meta">
          <p><strong>Published:</strong> {"publishedDate"}</p>
          <p><strong>Writer:</strong> {"writer"}</p>
          <p><strong>Penciller:</strong> {"penciller"}</p>
          <p><strong>Cover Artist:</strong> {"coverArtist"}</p>
        </div>
        <div className="ComicCard__Description">
          <p>{"description"}</p>
        </div>
        <a href="#" className="ComicCard__VariantLink">See Variant Covers</a>
      </div>
    </div>
    </div>
  );
}

export default BooksCard;
