import "./Card.css";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Card(props) {
  return (
  <div className="card col-sm-4 mb-3">
    <div className="row">
      <div className="col-sm-6">
      <img src={props.url} className="card-img-top" alt={props.name} />  
      </div>  
    </div>

    
    <div className="card-body">
      <h5 className="card-title">{props.name}</h5>
      <p className="card-text">{props.discription}</p>
      <p className="card-text"><small className="text-muted">MRP: <b>{props.mrp}</b></small></p>
      <button className="btn btn-primary">Buy Now</button>
    </div>
  </div>

  );
}
