import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

export default function Card(props){
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const stripe = useStripe();
  const elements = useElements();

  const handleBuyNow = async () => {
    setShowPaymentForm(true);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    // Create a payment method using Stripe Elements
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: 'card',
      card: elements.getElement(CardElement),
    });

    if (error) {
      console.error('Error creating payment method:', error);
      // Handle error (e.g., display error message to user)
    } else {
      console.log('Payment Method ID:', paymentMethod.id);
      // Send paymentMethod.id to your server to complete the payment
      // Example: const response = await fetch('/process-payment', { method: 'POST', body: JSON.stringify({ paymentMethodId: paymentMethod.id }) });
      // Handle server response (e.g., show success message, update UI)
    }
  };

  return (
    <div className="col-sm-4 p-2">
      <div className="card h-100">
        <div className="row h-100">
          <div className="col-xl-6">
            <img src={props.url} className="card-img h-100 img-thumbnail" alt={props.name} />
          </div>
          <div className="col-xl-6">
            <div className="card-body d-flex flex-column justify-content-between h-100">
              <h5 className="card-title">{props.name}</h5>
              <p className="card-text text-truncate">{props.discription}</p>
              <p className="card-text"><small className="text-muted">MRP: <b>{props.mrp}</b></small></p>
              <div className="card-footer">
                <button className="col-sm-5 btn btn-warning w-100 mb-1">Add To Cart</button>
                <button className="col-sm-5 btn btn-warning w-100" onClick={handleBuyNow}>Buy Now</button>
                {showPaymentForm && (
                  <form onSubmit={handleSubmit} className="mt-3">
                    <CardElement />
                    <button type="submit" className="btn btn-primary mt-3" disabled={!stripe}>
                      Pay Now
                    </button>
                  </form>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
