import './style/AddBooks.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import { toast } from 'react-toastify';

export default function AddBooks() {
  const [bookName, setBookName] = useState("");
  const [description, setDescription] = useState("");
  const [mrp, setMRP] = useState("");
  const [url, setUrl] = useState("");

  const sendData = async (event) => {
    event.preventDefault();
    const bookData = {
      bookName,
      description,
      mrp,
      url
    };

    try {
      const response = await fetch('http://localhost:5000/addBook', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bookData)
      });

      if (response.status === 201) {
        toast.success('Book added successfully!', {
          position: toast.POSITION.TOP_RIGHT
        });
      } else {
        toast.error('Failed to add book!', {
          position: toast.POSITION.TOP_RIGHT
        });
      }
    } catch (error) {
      toast.error('An error occurred!', {
        position: toast.POSITION.TOP_RIGHT
      });
    }
  };

  return (
    <div className="container min-vh-100">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <h1 className="text-center">Add Books</h1>
          <form onSubmit={sendData}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Book Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                value={bookName}
                onChange={(e) => setBookName(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Description</label>
              <textarea
                className="form-control"
                id="description"
                rows="3"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              ></textarea>
            </div>
            <div className="mb-3">
              <label htmlFor="mrp" className="form-label">MRP</label>
              <input
                type="number"
                className="form-control"
                id="mrp"
                value={mrp}
                onChange={(e) => setMRP(parseFloat(e.target.value))}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="url" className="form-label">Image URL</label>
              <input
                type="url"
                className="form-control"
                id="url"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary">Add Book</button>
          </form>
        </div>
      </div>
    </div>
  );
}
