import "./style/AddBooks.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react";

export default function AddBooks() {

  const [bookName, setBookName] = useState("");
  const [discription, setDiscription] = useState("");
  const [mRP, setMRP] = useState("");
  const [uRL, setUrl] = useState("");

  return (
  <div className="container min-vh-100">
    <div className="row">
      <div className="col-md-6 offset-md-3">
        <h1 className="text-center">Add Books</h1>
        <form>
          <div className="mb-3">
            <label for="name" className="form-label">Book Name</label>
            <input type="text" className="form-control" id="name" onChange={setBookName(this.target.value)}/>
          </div>
          <div className="mb-3">
            <label for="discription" className="form-label">Discription</label>
            <textarea className="form-control" id="discription" rows="3" onChange={setDiscription(this.target.value)}></textarea>
          </div>
          <div className="mb-3">
            <label for="mrp" className="form-label">MRP</label>
            <input type="number" className="form-control" id="mrp" />
          </div>
          <div className="mb-3">
            <label for="url" className="form-label">Image URL</label>
            <input type="url" className="form-control" id="url" />
          </div>
          <button type="submit" className="btn btn-primary">Add Book</button>
        </form>
      </div>
    </div>
  </div>

  );
}
